query_map.query:
SELECT 
                        GROUP_ID, GROUP_NAME, REG_DATE, ETC_COMMENT, USE_FLAG                        
                    FROM LLM_POLICY.USER_GROUP {where} order by GROUP_ID DESC limit {limit}

SELECT 
                        VIEW_ID, TYPE_ID, FILTER_NAME, REG_DATE, FILTER_CONDITION, ETC_COMMENT, USE_FLAG
                    FROM LLM_POLICY.FILTER_VIEW_INFO {where} order by VIEW_ID DESC limit {limit}

SELECT 
                        NO, SERVER_NAME, TOOL_NAME, REG_DATE, REG_TYPE, ETC_COMMENT, ALLOWED, REASON
                    FROM LLM_DATA.LOG_MCP_SERVER_BLOCK {where} order by REG_DATE DESC limit {limit}

SELECT 
                        NO, USER_ID, SESSION_ID, REG_DATE, ETC, ALLOWED, REASON
                    FROM LLM_DATA.LOG_USER_MONITOR {where} order by REG_DATE DESC limit {limit}

SELECT 
                        USER_ID, REG_DATE, EMAIL, AI_SERVICE, ETC_COMMENT, USE_FLAG
                    FROM app.AI_USER_ACCOUNT {where} order by REG_DATE DESC limit {limit}
